package operators;

public class Unarry {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=-2;
		int y=10;
		System.out.println(x);
		System.out.println(~x);
		System.out.println(~~x);
		System.out.println(~~~x);
		System.out.println(y);
		System.out.println(~y);
		System.out.println(~~y);
		System.out.println(~~~y);

	}

}
